/************* Typography ******************/
@font-face {
  font-family: 'IRANSansWeb';
  src: url('fonts/IRANSansWeb.eot?-wwn5ej');
  src: url('fonts/IRANSansWeb.eot?#iefix-wwn5ej') format('embedded-opentype'),
  url('__VITE_ASSET__B0O_Nwm4__') format('woff');
  font-weight: normal;
  font-style: normal;
}
*{
	padding:0;
	margin:0;
}
img{
	border:none;
	outline:none;
	max-width:100%;
}
a,a:active,a:focus,a:hover{
	outline:none;
	text-decoration:none;
}
a:hover{
	color:#223f84;
}
a{
	-webkit-transition:all 0.25s ease-in-out;
	-moz-transition:all 0.25s ease-in-out;
	-ms-transition:all 0.25s ease-in-out;
	-o-transition:all 0.25s ease-in-out;
	transition:all 0.25s ease-in-out;
	color:#333;
}
ul{
}
ol{
}
h1 {
	font-family: 'IRANSansWeb', sans-serif;
    color: #333;
    font-size: 18px;
    font-weight: 600;
    margin: 0 0 15px;
    text-transform: uppercase;
}
h2{
	font-size:17px;
	font-weight:600;
	margin:0 0 12px;
	text-transform:uppercase;
	line-height:24px;
	font-family: 'IRANSansWeb', sans-serif;
}
h3{
	font-size:15px;
	font-weight:600;
	margin:0 0 20px;
	text-transform:uppercase;
	font-family: 'IRANSansWeb', sans-serif;
	line-height:22px;
}
h4{
	font-family: 'IRANSansWeb', sans-serif;
	font-size:14px;
	margin:0 0 5px;
	line-height:19px;
	font-weight:normal;
}
p{
	font-family: 'IRANSansWeb', sans-serif;
	font-size: 14px;
	font-weight: 300;
	color: #aaaaaf;
	line-height: 24px;
}
input{
	border: 1px solid #eaeaea;
	margin-bottom: 20px;
	padding: 5px;
}
img{
	max-width:100%;
	height:auto}
.clear{
	clear:both;}
